import './App.scss';
import { Navbar } from './Navbar/Navbar';
import {Todos} from './Todos/Todo';
import { Input } from './Input/Input';

export class App extends React.Component
{
  constructor(props)
  {
   super(props);
    this.state = { 
      todos: [],
      todoItem : []
    };
  }
  create()
  {
    this.setState({showinput:true});
  }
  view(item)
  {
    console.log(item);
    console.log("View");
  }
  componentDidMount()
  {  var toJson = (response) => response.json() 
    // fetch('config/config.json').then((response) =>response.json())
    fetch('config/config.json').then(toJson)
      .then((config) => {
        // fetch(config.contacts_api_url).then((response) => response.json())
        fetch(config.contacts_api_url).then(toJson)
        .then((todos) => {
          console.log(todos);
          this.setState({todos});
        }
        )
      });
  }
  render() {
    console.log(this.state.todos);
     return (
        <paper> 
        <div>
        <span></span>

      <Navbar createHandler={this.create.bind(this)}> </Navbar>
      <Todos todos={this.state.todos}></Todos>
      {this.state.showinput?<Input> </Input>:null}
      </div>
      </paper>
    );
  }
}


