import React from 'react';
import './App.scss';

import { Navbar } from './Navbar/Navbar';
import {Todos} from './Todos/Todo';
import { Input } from './Input/Input';
export class App extends React.Component{

  constructor(props){
    super(props);
    this.state={
      showinput:false,      
      todos:[]
    };
  }

  create(){
    this.setState({showinput:true});
  }

  getTodos(){
    fetch("http://localhost:3001/todos").then((response) =>response.json()).then((todos) => {
    this.setState({todos});
    });
    this.forceUpdate();
  }

  removeElem(i){
    let _todos = this.state.todos.splice();
    _todos = _todos.splice(i,1);
    this.setState({todos:_todos});
  }
    
  componentDidMount(){
    this.getTodos();
  }

  render(){
    console.log(this.state.todos);
    return (      
          <div>          
          <Navbar createHandler={this.create.bind(this)}> </Navbar>
          <Todos todos={this.state.todos} updateHandler={this.getTodos.bind(this)}></Todos>
          {this.state.showinput?<Input> </Input>:null}
          </div>
    );    
  }
}
